import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-cars',
  templateUrl: './cars.component.html',
  styleUrls: ['./cars.component.css']
})
export class CarsComponent implements OnInit {

  name: string | undefined;
  speed: number | undefined;
  volume:string | undefined;
  hp: number | undefined;
  model: string | undefined;
  colors: Colors | any;
  options: string[] | any;
  isEdit: boolean = false;


  constructor() { }


  ngOnInit() {
    this.name = 'Aston Martin';
    this.volume = 'V12 6.0';
    this.hp = 565;
    this.speed = 275;
    this.model = 'Vanquish ';
    this.colors = {
      car: 'Серый',
      salon: 'Черный',
      wheels: 'Серебристые'
    };
    this.options = ['ABS', 'ASR', 'ESP', 'AutoParking'];
  }

  showEdit() {
    this.isEdit = !this.isEdit;
  }

  addOpt(option: any) {
    this.options.unshift(option);
    return false;
  }

  deleteOpt(option: any) {
    for (let i = 0; i < this.options.length; i ++) {
      if (this.options[i] == option)
        this.options.splice(i, 1);
      break;
    }
  }

  carSelect(carName: any) {
    if (carName == 'astonMartin') {
      this.name = 'Aston Martin';
      this.volume = 'V12 6.0';
      this.hp = 565;
      this.speed = 275;
      this.model = 'Vanquish ';
      this.colors = {
        car: 'Серый',
        salon: 'Черный',
        wheels: 'Серебристые'
      };
      this.options = ['ABS', 'ASR', 'ESP', 'AutoParking'];

    } else if (carName =='audi') {
      this.name = 'Audi';
      this.model = 'R8';
      this.volume = 'V8 4.2';
      this.hp = 540;
      this.speed = 280;
      this.colors = {
        car: 'Желтый',
        salon: 'Черный',
        wheels: 'Черный'
      };
      this.options = ['ABS', 'ESP', 'AutoParking'];

    } else if (carName =='ford') {
      this.name = 'Ford';
      this.volume = 'V8 4.7';
      this.hp = 306;
      this.speed = 290;
      this.model = 'GT40';
      this.colors = {
        car: 'Серый',
        salon: 'Черный',
        wheels: 'Черный'
      };
      this.options = ['ABS', 'ESP', 'AutoParking', 'ASR'];

      this.options = ['ABS', 'ESP', 'AutoParking'];
    } else if (carName =='bugatti') {
      this.name = 'Bugatti';
      this.volume = 'W16 8.0';
      this.hp = 1200;
      this.speed = 430;
      this.model = 'Veyron';
      this.colors = {
        car: 'Черно-оранжевый',
        salon: 'Черный',
        wheels: 'Оранжевый'
      };

      this.options = ['ABS', 'ESP', 'AutoParking'];
    } else if (carName =='corvette') {
      this.name = 'Corvette ';
      this.volume = 'V8 6.2';
      this.hp = 755;
      this.speed = 350;
      this.model = 'ZR1';
      this.colors = {
        car: 'Оранжевый',
        salon: 'Серый',
        wheels: 'Черный'
      };
      this.options = ['ABS', 'ESP', 'AutoParking'];

    } else if (carName =='koen') {
      this.name = 'Koenigsegg ';
      this.speed = 460;
      this.volume = 'V8 5.0';
      this.hp = 1100;
      this.model = 'Regera';
      this.colors = {
        car: 'Красный',
        salon: 'Красный',
        wheels: 'Черный'
      };
      this.options = ['ABS', 'ESP', 'AutoParking'];

    } else if (carName =='lambo') {
      this.name = 'Lamborghini ';
      this.volume = 'V10 4.2';
      this.hp = 602;
      this.speed = 380;
      this.model = 'Huracan ';
      this.colors = {
        car: 'Желтый',
        salon: 'Черный',
        wheels: 'Черный'
      };
      this.options = ['ABS', 'ESP', 'AutoParking'];

    } else if (carName =='mercedes') {
      this.name = 'Mercedes Benz';
      this.volume = 'V8 6.2';
      this.hp = 569;
      this.speed = 370;
      this.model = 'SLS AMG ';
      this.colors = {
        car: 'Серый',
        salon: 'Черный',
        wheels: 'Серый'
      };
      this.options = ['ABS', 'ESP', 'AutoParking'];

    } else {
      this.name = 'Jaguar ';
      this.speed = 330;
      this.volume = 'V6 5.2';
      this.hp = 495;
      this.model = 'F-Type Coupe';
      this.colors = {
        car: 'Оранжевый',
        salon: 'Черный',
        wheels: 'Серый'
      };
      this.options = ['ABS', 'ESP', 'ESR', 'AutoParking'];
    }
  }
}

interface Colors {
  car: string,
  salon: string,
  wheels: string
}
<div class="container">
  <br>
  <h4><strong>Автомобиль</strong>: {{name}}
  <small>{{model}}</small></h4>
  <div class="alert alert-primary">
    <p class="text-center">Характеристики автомобиля:</p>
    <ul>
      <li>Скорость: <strong>{{speed}}</strong> км/ч</li>
      <li>Объем двигателя: <strong>{{volume}}</strong> л.</li>
      <li>Лошадинные силы: <strong>{{hp}}</strong> л.с.</li>
      <li>Цвет авто: <strong>{{colors.car}}</strong></li>
      <li>Цвет салона: <strong>{{colors.salon}}</strong></li>
      <li>Цвет колес: <strong>{{colors.wheels}}</strong></li>
    </ul>
  </div>


  <button class="btn btn-danger" (click)="showEdit()">Редактировать </button>
  <div><br></div>
  <form *ngIf="isEdit">
    <h5>Редактирование объекта</h5>
    <div class="form-group">
      <input type="text" placeholder="Марка авто" class="form-control" [(ngModel)]="name" name="name"><br>
      <input type="text" placeholder="Модель авто" class="form-control" [(ngModel)]="model" name="model"><br>
      <input type="text" placeholder="Объем двигателя" class="form-control" [(ngModel)]="volume" name="volume"><br>
      <input type="number" placeholder="Лошадинные силы" class="form-control" [(ngModel)]="hp" name="hp"><br>
      <input type="number" placeholder="Скорость" class="form-control" [(ngModel)]="speed" name="speed"><br>
      <input type="text" placeholder="Цвет авто" class="form-control" [(ngModel)]="colors.car" name="car"><br>
      <input type="text" placeholder="Цвет салона" class="form-control" [(ngModel)]="colors.salon" name="salon"><br>
      <input type="text" placeholder="Цвет колес" class="form-control" [(ngModel)]="colors.wheels" name="wheels"><br>
    </div>
  </form>

  <h5>Дополнительные системы: </h5>
  <form *ngIf="isEdit">
    <form (submit)="addOpt(option.value)">
      <div class="form-group">
        <label for="option"> Добавление новой системы</label>
        <input type="text" id="option" placeholder="Введите текст" class="form-control" #option>
        <br>
        <button type="submit" class="btn btn-outline-success">Добавить</button><br>
      </div><br>
    </form>
  </form>
  <div class="alert alert-success">
    <ul>
      <li *ngFor="let opt of options">
        {{opt}} <button class="btn btn-warning btn-sm" (click)="deleteOpt(opt)">x</button>
      </li>
<!--      если хотим сделать список с индексами по каждой строке:-->
<!--      <li *ngFor="let opt of options; let i = index">{{i + 1}} - {{opt}}</li>-->

    </ul>
  </div>
  <h5>Выберите автомобиль: </h5>
  <div class="alert alert-warning choose">
    <button class="btn btn-danger mark" (click)="carSelect('astonMartin')">Aston Martin </button>
    <button class="btn btn-primary mark" (click)="carSelect('audi')">Audi </button>
    <button class="btn btn-success mark" (click)="carSelect('ford')">Ford</button>
    <button class="btn btn-danger mark" (click)="carSelect('bugatti')">Bugatti</button>
    <button class="btn btn-primary" (click)="carSelect('corvette')">Corvette </button>
    <button class="btn btn-success" (click)="carSelect('koen')">Koenigsegg</button>
    <button class="btn btn-danger" (click)="carSelect('lambo')">Lamborghini</button>
    <button class="btn btn-primary" (click)="carSelect('mercedes')">Mercedes Benz </button>
    <button class="btn btn-success" (click)="carSelect('jaguar')">Jaguar </button>
  </div>
</div>
