import { Component, Input } from '@angular/core';
import { Carinfo} from "../../carinfo";

@Component({
  selector: 'app-car-info',
  template: `
    <section class="listing">
      <img class="listing-photo" [src]="carinfo.photo" alt="Exterior photo of {{carinfo.name}}">
      <h2 class="listing-heading">{{ carinfo.name }}</h2>
      <p class="listing-location">{{ carinfo.city}}, {{carinfo.state }}</p>
      <div class="bnav-item nav-link nav_bar navigation_bar" routerLink="{{carinfo.address}}">Переход</div>
      <a class="nav-item nav-link nav_bar navigation_bar" routerLink="/about">Подробней</a>
    </section>

  `,
  styleUrls: ['./car-info.component.css']
})
export class CarInfoComponent {
  @Input() carinfo!: Carinfo;
}
