import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-cars',
  templateUrl: './cars.component.html',
  styleUrls: ['./cars.component.css']
})
export class CarsComponent implements OnInit {

  name: string | undefined;
  speed: number | undefined;
  model: string | undefined;
  colors: Colors | any;
  options: string[] | any;


  constructor() { }


  ngOnInit() {
    this.name = 'BMW';
    this.speed = 250;
    this.model = 'X5';
    this.colors = {
      car: 'Белый',
      salon: 'Бежевый',
      wheels: 'Серебристые'
    };
    this.options = ['ABS', 'ASR', 'ESP', 'AutoParking'];
  }
}

interface Colors {
  car: string,
  salon: string,
  wheels: string
}
<div class="container">
  <br>
  <h4><mark>Автомобиль:</mark>{{name}}
  <small>{{model}}</small></h4>
  <div class="alert alert-primary">
    Характеристики автомобиля:
    <ul>
      <li>Скорость: <strong>{{speed}}</strong> км/ч</li>
      <li>Цвет авто: <strong>{{colors.car}}</strong></li>
      <li>Цвет салона: <strong>{{colors.salon}}</strong></li>
      <li>Цвет колес: <strong>{{colors.wheels}}</strong></li>
    </ul>
  </div>

  <h5>Дополнительные системы: </h5>
  <div class="alert alert-success">
    <ul>
      <li *ngFor="let opt of options">{{opt}}</li>
<!--      если хотим сделать список с индексами по каждой строке:-->
<!--      <li *ngFor="let opt of options; let i = index">{{i + 1}} - {{opt}}</li>-->

    </ul>
  </div>
  <h5>Выберите автомобиль: </h5>
  <div class="alert alert-warning">
    <button class="btn btn-danger" (click)="bmwSelect" )>BMW </button>
    <button class="btn btn-primary">AUDI </button>
    <button class="btn btn-success">VOLKSWAGEN</button>
  </div>
</div>
