import { Component } from '@angular/core';

@Component({
  selector: 'app-details',
  template: `
    <button class="btn btn-outline-danger back" routerLink="/catalog" >Назад </button>
    <p>
      details works!
    </p>
    <!DOCTYPE html>
    <html lang="en">
    <head>
      <meta charset="UTF-8" />
      <meta http-equiv="X-UA-Compatible" content="IE=edge" />
      <meta name="viewport" content="width=device-width, initial-scale=1.0" />
      <title>Image Slider</title>
      <link
        rel="stylesheet"
        href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/5.15.3/css/all.min.css"
      />
      <link rel="stylesheet" href="details.component.css" />
    </head>
    <body>
    <div class="container">

      <div class="mySlides">
        <img src="https://st.automobilemag.com/uploads/sites/10/2015/09/2015-Audi-R8-Competition-Edition-front-three-quarter.jpg" style="width:100% ; height:50vh" >
      </div>

      <div class="mySlides">
        <img src="http://vsegda-pomnim.com/uploads/posts/2022-04/1650916178_25-vsegda-pomnim-com-p-krasivie-peizazhi-gor-foto-32.jpg">
      </div>

      <div class="mySlides">
        <img src="https://mykaleidoscope.ru/x/uploads/posts/2022-10/1666361489_3-mykaleidoscope-ru-p-peizazhi-prirodi-krasivo-3.jpg" style="width:100% ; height:50vh">
      </div>

      <div class="mySlides">
        <img src="https://mykaleidoscope.ru/x/uploads/posts/2022-10/1666361514_50-mykaleidoscope-ru-p-peizazhi-prirodi-krasivo-55.jpg" style="width:100% ; height:50vh">
      </div>

      <div class="mySlides">
        <img src="https://phonoteka.top/uploads/posts/2022-09/1663894449_40-phonoteka-org-p-oboi-na-telefon-peizazhi-prirodi-vkontakte-44.jpg" style="width:100% ; height:50vh">
      </div>


      <a class="prev" onclick="plusSlides(-1)">❮</a>
      <a class="next" onclick="plusSlides(1)">❯</a>

      <div class="caption-container">
        <p id="caption"></p>
      </div>


    </div>

    <script>
      let slideIndex = 1;
      showSlides(slideIndex);

      function plusSlides(n) {
        showSlides(slideIndex += n);
      }

      function currentSlide(n) {
        showSlides(slideIndex = n);
      }

      let slideInterval = setInterval(() => {
        plusSlides(1);
      }, 8000);

      let slideshowContainer = document.getElementsByClassName('slideshow-container')[0];
      slideshowContainer.addEventListener('mouseenter', () => {
        clearInterval(slideInterval);
      });

      slideshowContainer.addEventListener('mouseleave', () => {
        slideInterval = setInterval(() => {
          plusSlides(1);
        }, 8000);
      });

      function showSlides(n) {
        let i;
        let slides = document.getElementsByClassName("mySlides");
        let dots = document.getElementsByClassName("demo");
        let captionText = document.getElementById("caption");
        if (n > slides.length) {slideIndex = 1}
        if (n < 1) {slideIndex = slides.length}
        for (i = 0; i < slides.length; i++) {
          slides[i].style.display = "none";
        }
        for (i = 0; i < dots.length; i++) {
          dots[i].className = dots[i].className.replace(" active", "");
        }
        slides[slideIndex-1].style.display = "block";
        dots[slideIndex-1].className += " active";
        captionText.innerHTML = dots[slideIndex-1].alt;
      }
    </script>
    </body>
    </html>
  `,
  styleUrls: ['./details.component.css']
})
export class DetailsComponent {

}
